import { useCallback, useState } from 'react';

import type { Dispatch, SetStateAction } from 'react';

type UseBooleanReturn = {
  value: boolean;
  setValue: Dispatch<SetStateAction<boolean>>;
  setTrue: () => void;
  setFalse: () => void;
  toggle: () => void;
};

export const useBoolean = (defaultValue = false): UseBooleanReturn => {
  if (typeof defaultValue !== 'boolean') {
    throw new Error('defaultValue must be `true` or `false`');
  }
  const [value, setValue] = useState(defaultValue);

  const setTrue = useCallback(() => {
    setValue(true);
  }, []);

  const setFalse = useCallback(() => {
    setValue(false);
  }, []);

  const toggle = useCallback(() => {
    setValue((x) => !x);
  }, []);

  return { value, setValue, setTrue, setFalse, toggle };
};

import { Dispatch, SetStateAction, useCallback, useState } from 'react';

export const useToggle = (defaultValue?: boolean): [boolean, () => void, Dispatch<SetStateAction<boolean>>] => {
  const [value, setValue] = useState(!!defaultValue);

  const toggle = useCallback(() => {
    setValue((x) => !x);
  }, []);

  return [value, toggle, setValue];
};

import { useEffect, useState, useCallback } from 'react';

interface UseKeyboardOptions {
  onKeyDown?: (event: KeyboardEvent) => void;
  onKeyUp?: (event: KeyboardEvent) => void;
  onKeyPress?: (event: KeyboardEvent) => void; // Note: keypress is deprecated but included if needed
}

export const useKeyboard = ({ onKeyDown, onKeyUp, onKeyPress }: UseKeyboardOptions = {}) => {
  const [pressedKeys, setPressedKeys] = useState<Set<string>>(new Set());

  const handleKeyDown = useCallback(
    (event: KeyboardEvent) => {
      setPressedKeys((prev) => {
        if (!prev.has(event.key)) {
          const newSet = new Set(prev);
          newSet.add(event.key);
          return newSet;
        }
        return prev;
      });
      onKeyDown?.(event);
    },
    [onKeyDown]
  );

  const handleKeyUp = useCallback(
    (event: KeyboardEvent) => {
      setPressedKeys((prev) => {
        if (prev.has(event.key)) {
          const newSet = new Set(prev);
          newSet.delete(event.key);
          return newSet;
        }
        return prev;
      });
      onKeyUp?.(event);
    },
    [onKeyUp]
  );

  const handleKeyPress = useCallback(
    (event: KeyboardEvent) => {
      onKeyPress?.(event);
    },
    [onKeyPress]
  );

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);
    if (onKeyPress) {
      window.addEventListener('keypress', handleKeyPress);
    }

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
      if (onKeyPress) {
        window.removeEventListener('keypress', handleKeyPress);
      }
    };
  }, [handleKeyDown, handleKeyUp, handleKeyPress, onKeyPress]);

  return {
    pressedKeys, // Set of currently pressed keys
  };
};

import { useEffect, useState } from 'react';

export const useOnline = (): boolean => {
  const [isOnline, setIsOnline] = useState<boolean>(navigator.onLine);

  useEffect(() => {
    const updateOnline = () => setIsOnline(true);
    const updateOffline = () => setIsOnline(false);

    window.addEventListener('online', updateOnline);
    window.addEventListener('offline', updateOffline);

    return () => {
      window.removeEventListener('online', updateOnline);
      window.removeEventListener('offline', updateOffline);
    };
  }, []);

  return isOnline;
};

import { useState, useEffect } from 'react';

interface WindowSize {
  width: number;
  height: number;
}

export const useWindowResize = (): WindowSize => {
  const [size, setSize] = useState<WindowSize>({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    const handleResize = () => {
      setSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener('resize', handleResize);

    // Call handler right away so state gets updated with initial size
    handleResize();

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return size;
};

import { useState, useEffect } from 'react';

interface ScrollPosition {
  scrollX: number;
  scrollY: number;
}

export const useScroll = (): ScrollPosition => {
  const [scrollPos, setScrollPos] = useState<ScrollPosition>({
    scrollX: window.scrollX,
    scrollY: window.scrollY,
  });

  useEffect(() => {
    const handleScroll = () => {
      setScrollPos({
        scrollX: window.scrollX,
        scrollY: window.scrollY,
      });
    };

    window.addEventListener('scroll', handleScroll, { passive: true });

    // Optional: update on mount in case scroll position changed before hook
    handleScroll();

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return scrollPos;
};

