import { useEffect, useState } from 'react';

interface BatteryManager extends EventTarget {
  charging: boolean;
  chargingTime: number; // seconds until fully charged, or 0 if fully charged
  dischargingTime: number; // seconds until fully discharged, or Infinity if charging
  level: number; // battery level between 0 (empty) and 1 (full)

  // Events
  onchargingchange: ((this: BatteryManager, ev: Event) => any) | null;
  onchargingtimechange: ((this: BatteryManager, ev: Event) => any) | null;
  ondischargingtimechange: ((this: BatteryManager, ev: Event) => any) | null;
  onlevelchange: ((this: BatteryManager, ev: Event) => any) | void;
}

interface BatteryStatus {
  charging: boolean;
  level: number; // 0 to 1
  chargingTime: number; // seconds
  dischargingTime: number; // seconds
}

export function useBatteryStatus() {
  const [battery, setBattery] = useState<BatteryStatus | null>(null);

  useEffect(() => {
    let batteryManager: BatteryManager | null = null;

    const updateBattery = (batt: BatteryManager) => {
      setBattery({
        charging: batt.charging,
        level: batt.level,
        chargingTime: batt.chargingTime,
        dischargingTime: batt.dischargingTime,
      });
    };

    (navigator as any).getBattery?.().then((batt: BatteryManager) => {
      batteryManager = batt;
      updateBattery(batt);

      batt.addEventListener('chargingchange', () => updateBattery(batt));
      batt.addEventListener('levelchange', () => updateBattery(batt));
      batt.addEventListener('chargingtimechange', () => updateBattery(batt));
      batt.addEventListener('dischargingtimechange', () => updateBattery(batt));
    });

    return () => {
      if (!batteryManager) return;
      batteryManager.removeEventListener('chargingchange', () => updateBattery(batteryManager!));
      batteryManager.removeEventListener('levelchange', () => updateBattery(batteryManager!));
      batteryManager.removeEventListener('chargingtimechange', () => updateBattery(batteryManager!));
      batteryManager.removeEventListener('dischargingtimechange', () => updateBattery(batteryManager!));
    };
  }, []);

  return battery;
}

import { useState, useEffect } from 'react';

interface BrowserInfo {
  name: string;
  version: string;
  userAgent: string;
}

export const useBrowser = (): BrowserInfo => {
  const [info, setInfo] = useState<BrowserInfo>({
    name: 'unknown',
    version: '',
    userAgent: navigator.userAgent,
  });

  useEffect(() => {
    const ua = navigator.userAgent;

    let name = 'unknown';
    let version = '';

    if (/firefox\/(\d+)/i.test(ua)) {
      name = 'Firefox';
      version = ua.match(/firefox\/(\d+)/i)?.[1] ?? '';
    } else if (/chrome\/(\d+)/i.test(ua) && !/edg/i.test(ua)) {
      name = 'Chrome';
      version = ua.match(/chrome\/(\d+)/i)?.[1] ?? '';
    } else if (/safari\/(\d+)/i.test(ua) && !/chrome/i.test(ua)) {
      name = 'Safari';
      version = ua.match(/version\/(\d+)/i)?.[1] ?? '';
    } else if (/edg\/(\d+)/i.test(ua)) {
      name = 'Edge';
      version = ua.match(/edg\/(\d+)/i)?.[1] ?? '';
    } else if (/opera\/(\d+)/i.test(ua)) {
      name = 'Opera';
      version = ua.match(/opera\/(\d+)/i)?.[1] ?? '';
    }

    setInfo({
      name,
      version,
      userAgent: ua,
    });
  }, []);

  return info;
};

import { useState, useEffect } from 'react';

export const useLanguage = (): string => {
  const [language, setLanguage] = useState<string>(navigator.language || 'en');

  useEffect(() => {
    setLanguage(navigator.language);
  }, [navigator.language]);

  return language;
};

