  }, []);

  return size;
};

import { useCallback, useState } from 'react';

type ClipboardState = {
  copiedText: string;
  error: string | null;
  isSupported: boolean;
  copy: (text: string) => Promise<boolean>;
  read: () => Promise<string>;
};

export const useClipboard = (): ClipboardState => {
  const [copiedText, setCopiedText] = useState('');
  const [error, setError] = useState<string | null>(null);

  const isSupported = !!navigator?.clipboard;

  const copy = useCallback(
    async (text: string) => {
      if (!isSupported) {
        setError('Clipboard API not supported');
        return false;
      }

      try {
        await navigator.clipboard.writeText(text);
        setCopiedText(text);
        setError(null);
        return true;
      } catch (error) {
        setError((error as Error).message);
        return false;
      }
    },
    [isSupported]
  );

  const read = useCallback(async () => {
    if (!isSupported) {
      setError('Clipboard API not supported');
      return '';
    }

    try {
      const text = await navigator.clipboard.readText();
      setCopiedText(text);
      setError(null);
      return text;
    } catch (error) {
      setError((error as Error).message);
      return '';
    }
  }, [isSupported]);

  return {
    copiedText,
    error,
    isSupported,
    copy,
    read,
  };
};

import { useEffect, useState } from 'react';

type GeolocationState = {
  coords: GeolocationCoordinates | null;
  timestamp: number | null;
  error: Error | GeolocationPositionError | null;
};

export const useGeolocation = (watch: boolean = false, options?: PositionOptions) => {
  const [state, setState] = useState<GeolocationState>({
    coords: null,
    timestamp: null,
    error: null,
  });

  useEffect(() => {
    if (!navigator.geolocation) {
      setState((s) => ({ ...s, error: new Error('Geolocation is not supported') }));
      return;
    }

    const onSuccess = (pos: GeolocationPosition) => {
      setState({
        coords: pos.coords,
        timestamp: pos.timestamp,
        error: null,
      });
    };

    const onError = (err: GeolocationPositionError) => {
      setState((s) => ({ ...s, error: err }));
    };

    let watchId: number;

    if (watch) {
      watchId = navigator.geolocation.watchPosition(onSuccess, onError, options);
    } else {
      navigator.geolocation.getCurrentPosition(onSuccess, onError, options);
    }

    return () => {
      if (watch && watchId !== undefined) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, [watch, options]);

  return state;
};

import { useEffect, useState } from 'react';

export const useLocalStorage = (key: string, initialValue: string) => {
  // Get from local storage then parse stored json or return initial value
  const readValue = () => {
    if (typeof window === 'undefined') return initialValue;
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.warn(`Error reading localStorage key “${key}”:`, error);
      return initialValue;
    }
  };

  const [storedValue, setStoredValue] = useState(readValue);

  // Update localStorage when the state changes
  useEffect(() => {
    if (typeof window === 'undefined') return;
    try {
      window.localStorage.setItem(key, JSON.stringify(storedValue));
    } catch (error) {
      console.warn(`Error setting localStorage key “${key}”:`, error);
    }
  }, [key, storedValue]);

  return [storedValue, setStoredValue];
};

